import React from 'react';
import './AboutSec.css';
import ProfilePic from '../assets/aboutIMG.jpeg'; // Replace with your profile image path

const AboutSec = () => {
    return (
        <section className="about-section">
            <div className="parallax-background"></div> {/* Parallax forest background */}
            <div className="mist-overlay"></div> {/* Misty effect overlay */}
            <div className="about-content">
                <div className="profile-container">
                    <img src={ProfilePic} alt="Profile" className="profile-image" />
                </div>
                <div className="text-content">
                    <h2 className="title">Who am I?</h2>
                    <p className="description">
                        I’m more than just a dedicated student; I’m passionate about programming, 
                        creative design, and building solutions that make an impact. With a background 
                        in both web development and design, I bring a unique blend of technical and 
                        creative skills to every project I work on.
                    </p>
                </div>
            </div>
        </section>
    );
};

export default AboutSec;
